// Arctic Shorex Sample Response
const customerName = $input.item.json.body.name || 'Valued Traveler';
const inquiry = $input.item.json.body.message || 'General inquiry';

const response = {
  status: 'success',
  message: `Hello ${customerName}! Thank you for your interest in Arctic Shorex Travel Agency.`,
  inquiry: inquiry,
  nextSteps: 'Our Arctic travel specialists will contact you within 24 hours.',
  contact: {
    email: 'support@articshorex.com',
    phone: '+1-555-ARCTIC',
    website: 'www.articshorex.com'
  },
  timestamp: new Date().toISOString()
};

return { response };
