// Arctic Shorex Customer Support Response
const customerName = $input.item.json.body.name || $input.item.json.name || 'Valued Traveler';
const customerEmail = $input.item.json.body.email || $input.item.json.email || 'customer@example.com';
const inquiry = $input.item.json.body.message || $input.item.json.message || 'General inquiry about Arctic travel';
const inquiryType = $input.item.json.body.type || 'general';

// Create Arctic Shorex response
const response = {
  status: 'success',
  ticketId: `AS-${Date.now()}`,
  customer: {
    name: customerName,
    email: customerEmail
  },
  inquiry: {
    message: inquiry,
    type: inquiryType,
    receivedAt: new Date().toISOString()
  },
  response: {
    message: `Hello ${customerName}! Thank you for your interest in Arctic Shorex Travel Agency. We specialize in unique Arctic expeditions and Northern Lights tours.`,
    nextSteps: 'Our Arctic travel specialists will contact you within 24 hours to discuss your adventure.',
    specialties: [
      'Northern Lights Tours',
      'Arctic Expeditions', 
      'Polar Wildlife Viewing',
      'Ice Hotel Experiences',
      'Arctic Photography Tours'
    ]
  },
  contact: {
    email: 'support@articshorex.com',
    phone: '+1-555-ARCTIC',
    website: 'www.articshorex.com',
    emergency: '+1-555-ARCTIC-911'
  },
  timestamp: new Date().toISOString()
};

return { response };
